package com.controller;import com.model.Account;import com.service.AccountService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.servlet.ModelAndView;import javax.servlet.http.HttpSession;@Controllerpublic class LoginController {    @Autowired    AccountService accountService;    @Autowired    HttpSession httpSession;    @GetMapping("/login")    public String showLogin(){        return "login";    }    @PostMapping("/login")    public String login(@RequestParam String username, @RequestParam String password){        Account account = accountService.checkLogin(username,password);        if (account == null){            return "redirect:/login";        }else {            httpSession.setAttribute("account", account);            return "redirect:/blog";        }    }    @GetMapping("/register")    public ModelAndView saveAccount() {        ModelAndView modelAndView = new ModelAndView("/register");        return modelAndView;    }    @PostMapping("/register")    public ModelAndView save(@ModelAttribute Account account) {        ModelAndView modelAndView = new ModelAndView("redirect:/login") ;        accountService.register(account);        return  modelAndView;    }}